mapper.select:
select dish.*, category.name category_name from dish left join category on dish.category_id = category.id

    and dish.name like concat('%', #{name}, '%')
    and dish.category_id = #{categoryId}
    and dish.status = #{status}

order by update_time desc

mapper.update:
update dish
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}
